.city_select_lhsl .tab_box { height:22px; margin-bottom:6px; border-bottom:2px solid #ccc; }
.city_select_lhsl .tab_box li { position:relative; float:left; display:inline; margin-right:10px; line-height:22px; color:#06c; cursor:pointer; }
.city_select_lhsl .tab_box li b { display:none; }
.city_select_lhsl .tab_box li span { padding:0 3px; }
.city_select_lhsl .tab_box .selected { border-bottom:2px solid #06c; margin-bottom:-2px; font-weight:bold; color:#333; }
.city_select_lhsl .tab_box .selected b { position:absolute; top:23px; left:50%; display:block; width:0; height:0; margin-left:-5px; overflow:hidden; font-size:0; line-height:0; border-color:#06c transparent transparent transparent; border-style: solid dashed dashed dashed; border-width:5px; }
.city_select_lhsl .city_item { display:inline-block; }
.city_select_lhsl .city_item { display:block; overflow:hidden;}
.city_select_lhsl .city_item a { float:left; width:55px; overflow:hidden; height:22px; padding-left:5px; border:1px solid #fff; white-space:nowrap; line-height:22px; }
.city_select_lhsl .city_item a:hover { border:1px solid #ACCCEF; background-color:#E8F4FF; text-decoration:none; }
